const homePage=()=>{
    const {userData,getInfo}=useUser()
    const [feedData, setFeedData] = useState([])
    const [loading, setLoading] = useState(false)
    const [page, setPage] = useState(1)
    const flatListRef = useRef(null)

    // 랜덤 소개글 생성 함수
    const getRandomDescription = () => {
        const descriptions = [
            "오늘 하루도 고생 많았어요! 🌟",
            "새로운 프로젝트 시작! 열심히 해보겠습니다 💪",
            "커피 한 잔의 여유로움... ☕",
            "팀원들과 함께하는 시간이 가장 즐거워요 👥",
            "코딩하는 새벽의 고요함이 좋아요 🌙",
            "오늘 배운 것들을 정리해보는 시간 📚",
            "성취감 가득한 하루였네요! ✨",
            "동료들과의 협업이 만들어낸 결과물 🤝",
            "새로운 기술 스택에 도전하는 중입니다 🚀",
            "일과 휴식의 완벽한 밸런스 ⚖️",
            "창의적인 아이디어가 떠오르는 순간 💡",
            "디버깅 성공! 뿌듯한 순간입니다 🐛✅",
            "사용자 피드백을 반영한 새로운 기능 🔄",
            "마감을 앞둔 열정적인 개발 시간 ⏰",
            "좋은 사람들과 함께하는 프로젝트 ❤️"
        ]
        return descriptions[Math.floor(Math.random() * descriptions.length)]
    }

    // 가짜 피드 데이터 생성 함수
    const generateFeedData = (pageNum) => {
        const users = ['김철수', '이영희', '박민수', '정지은', '최상훈']
        const newData = []

        for(let i = 0; i < 5; i++) {
            const feedId = (pageNum - 1) * 5 + i + 1
            newData.push({
                id: feedId,
                userName: users[i % users.length],
                time: `${Math.floor(Math.random() * 12) + 1}시간 전`,
                description: getRandomDescription(),
                image: null // 나중에 실제 이미지 URL 추가
            })
        }
        return newData
    }

    // 초기 피드 로드
    const loadInitialFeed = () => {
        const initialData = generateFeedData(1)
        setFeedData(initialData)
    }

    // 추가 피드 로드 (무한스크롤)
    const loadMoreFeed = () => {
        if (loading) return

        setLoading(true)

        // API 호출 시뮬레이션 (1초 딜레이)
        setTimeout(() => {
            const nextPage = page + 1
            const newData = generateFeedData(nextPage)

            setFeedData(prevData => [...prevData, ...newData]) // 기존 데이터에 추가
            setPage(nextPage)
            setLoading(false)
        }, 1000)
    }

    // 탭에 포커스될 때마다 피드 초기화
    useFocusEffect(
        useCallback(() => {
            // 사용자 정보 가져오기
            getInfo()

            // 피드 데이터 초기화
            setFeedData([])
            setPage(1)
            setLoading(false)

            // 스크롤 위치를 맨 위로 초기화
            if (flatListRef.current) {
                flatListRef.current.scrollToOffset({ offset: 0, animated: false })
            }

            // 초기 피드 로드
            loadInitialFeed()
        }, [])
    )

    return(
        <View style={{flex:1}}>
            <View style={{
                flex:1,
                flexDirection: 'row',
                justifyContent: 'space-between',
                alignItems: 'center',
                paddingHorizontal: 20
            }}>
                <GitTeolLogo />

                <View style={{flexDirection: 'row'}}>
                    <TouchableOpacity
                        style={{marginRight: 20}}
                        activeOpacity={0.6}
                        onPress={() => {
                            console.log('SMS 아이콘 클릭!')
                            // 나중에 SMS 기능 추가
                        }}
                    >
                        <SmsIcon />
                    </TouchableOpacity>

                    <TouchableOpacity
                        activeOpacity={0.6}
                        onPress={() => {
                            console.log('Star 아이콘 클릭!')
                            // 나중에 즐겨찾기 기능 추가
                        }}
                    >
                        <StarIcon />
                    </TouchableOpacity>
                </View>
            </View>

            <View style={{flex:18, backgroundColor: '#f8f9fa'}}>
                <FlatList
                    ref={flatListRef}
                    data={feedData}
                    keyExtractor={(item) => item.id.toString()}
                    renderItem={({ item }) => <FeedItem item={item} />}
                    onEndReached={loadMoreFeed}
                    onEndReachedThreshold={0.1}
                    showsVerticalScrollIndicator={false}
                    contentContainerStyle={{
                        paddingTop: 10,
                        paddingBottom: 20
                    }}
                    ListFooterComponent={() => {
                        if (loading) {
                            return (
                                <View style={{
                                    padding: 20,
                                    alignItems: 'center'
                                }}>
                                    <ActivityIndicator size="large" color="#007AFF" />
                                    <Text style={{
                                        marginTop: 10,
                                        color: '#666',
                                        fontSize: 14
                                    }}>
                                        피드를 불러오는 중...
                                    </Text>
                                </View>
                            )
                        }
                        return null
                    }}
                />
            </View>
        </View>
    )
}

export default homePage